import { useEffect } from 'react';
import { useItemContext } from '../../context/ItemContext';
import { fetchItems } from '../../services/api';
import { Loading } from '../../shared/Loading';
import { useNavigate } from 'react-router';
import { ListItem } from '../../components/_ListItems/ListItem';
import { ItemModal } from '../../components/_ListItems/ItemModal';
import { Item } from '../../types/ItemTypes';
import { useInfiniteScroll } from '../../hooks/useInfiniteScroll';
import './styles.scss';

const ListItems = () => {
  const navigate = useNavigate();
  const { state, dispatch } = useItemContext();
  const { searchQuery, selectedCategory, items, currentPage, hasMore } = state;

  const filteredItems = items.filter(item => {
    const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory ? item.category === selectedCategory : true;
    return matchesSearch && matchesCategory;
  });

  const loadItems = async (page: number) => {
    dispatch({ type: 'SET_LOADING', payload: true });
    const newItems: Item[] = await fetchItems(page) as Item[]; 
    dispatch({ type: 'SET_ITEMS', payload: [...items, ...newItems] });
    dispatch({ type: 'SET_LOADING', payload: false });
  };

  useEffect(() => {
    loadItems(currentPage); 
  }, [currentPage, dispatch]);

  const handleEditItem = (item: Item) => {
    dispatch({ type: 'OPEN_MODAL', payload: item });
  };

  const handleRemoveItem = (id: number) => {
    dispatch({ type: 'REMOVE_ITEM', payload: id });
  };

  const handleViewItem = (id: number) => {
    navigate(`/items/${id}`);
  };

  const loadMoreItems = () => {
    if (hasMore) {
      dispatch({ type: 'INCREMENT_PAGE' });
      loadItems(currentPage + 1); // Load next page
    }
  };

  const lastElementRef = useInfiniteScroll(loadMoreItems);

  return (
    <div className="item-list">
      {state.loading && <Loading />}
      {filteredItems.map((item: Item, index: number) => (
        <ListItem 
          key={item.id} 
          item={item} 
          onView={handleViewItem} 
          onEdit={handleEditItem} 
          onDelete={handleRemoveItem} 
          ref={index === filteredItems.length - 1 ? lastElementRef : null} // Set ref on last item
        />
      ))}
      <ItemModal />
    </div>
  );
};

export default ListItems;
