import React, { useState, useEffect } from 'react';
import './styles.scss';
import { Button } from '../../../shared/Button';
import { CreateOrEditItemProps, Item } from '../../../types/ItemTypes';
import { fetchCategories } from '../../../services/api';
import InputField from './InputField';
import SelectField from './SelectField';

export const CreateOrEditItem: React.FC<CreateOrEditItemProps> = ({ item, onSave, onCancel }) => {
  const initialFormData: Item = {
    id: item?.id || Date.now(),
    title: item?.title || '',
    thumbnail: item?.thumbnail || '',
    description: item?.description || '',
    fullDescription: item?.fullDescription || '',
    category: item?.category || '',
    rating: item?.rating || 0,
  };

  const [formData, setFormData] = useState<Item>(initialFormData);
  const [categories, setCategories] = useState<{ id: number, name: string }[]>([]);
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  useEffect(() => {
    if (item) {
      setFormData(item);
    }
  }, [item]);

  useEffect(() => {
    const loadCategories = async () => {
      const res = await fetchCategories();
      setCategories(res as { id: number, name: string }[]);
    };

    loadCategories();
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({ ...prevState, [name]: value }));
    setErrors((prev) => ({ ...prev, [name]: '' }));
  };

  const validateForm = () => {
    const newErrors: { [key: string]: string } = {};
    if (!formData.title) newErrors.title = 'Title is required';

    const urlRegex = new RegExp(/^(http|https):\/\/[^ "]+$/);
    if (!urlRegex.test(formData.thumbnail)) newErrors.thumbnail = 'Thumbnail URL is invalid';

    if (!formData.description) newErrors.description = 'Short Description is required';
    if (!formData.fullDescription) newErrors.fullDescription = 'Full Description is required';
    if (!formData.category) newErrors.category = 'Category is required';

    if (formData.rating < 0 || formData.rating > 5) {
      newErrors.rating = 'Rating must be between 0 and 5';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = () => {
    if (validateForm()) {
      onSave(formData);
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>{item ? 'Edit Item' : 'Create Item'}</h2>
        <form>
          <InputField
            label="Title"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            error={errors.title}
          />
          <InputField
            label="Thumbnail URL"
            id="thumbnail"
            name="thumbnail"
            value={formData.thumbnail}
            onChange={handleChange}
            error={errors.thumbnail}
          />
          <InputField
            label="Short Description"
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            error={errors.description}
          />
          <InputField
            label="Full Description"
            id="fullDescription"
            name="fullDescription"
            value={formData.fullDescription || ''}
            onChange={handleChange}
            type="textarea"
            error={errors.fullDescription}
          />
          <SelectField
            label="Category"
            id="category"
            name="category"
            value={formData.category || ''}
            onChange={handleChange}
            options={categories}
            error={errors.category}
          />
          <InputField
            label="Rating"
            id="rating"
            name="rating"
            value={formData.rating || 0}
            onChange={handleChange}
            type="number"
            error={errors.rating}
          />
          <div className="form-actions">
            <Button onClick={handleSave}>{item ? 'Edit' : 'Save'}</Button>
            <Button variant="danger" onClick={onCancel}>Cancel</Button>
          </div>
        </form>
      </div>
    </div>
  );
};
