import React from 'react';
import '../styles.scss';

interface InputFieldProps {
  label: string;
  id: string;
  name: string;
  value: string | number;
  onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  error?: string;
  type?: string;
  onBlur?: (e: React.FocusEvent<HTMLInputElement | HTMLTextAreaElement>) => void; // Update type
}

const InputField: React.FC<InputFieldProps> = ({ label, id, name, value, onChange, error, type = 'text', onBlur }) => (
  <div className="form-group">
    <label htmlFor={id}>{label}</label>
    {type === 'textarea' ? (
      <textarea id={id} name={name} value={value} onChange={onChange} onBlur={onBlur}></textarea>
    ) : (
      <input type={type} id={id} name={name} value={value} onChange={onChange} onBlur={onBlur} />
    )}
    {error && <span className="error">{error}</span>}
  </div>
);

export default InputField;
