import React, { lazy, Suspense, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import {ItemProvider} from './context/ItemContext';
import { Loading } from './shared/Loading';
import { ErrorBoundary } from './shared/ErrorBoundary';
import './App.scss';
import Header from './components/Header';

const ListItems = lazy(() => import('./pages/ListItems'));
const DetailedListItem = lazy(() => import('./pages/DetailedListItem'));

const App: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleFilter = (category: string) => {
    setSelectedCategory(category);
  };
  return (
    <ItemProvider >
      <ErrorBoundary>
        <Suspense fallback={<Loading />}>
        <Header onSearch={handleSearch} onFilter={handleFilter} />
          <Routes>
          <Route 
              path="/items" 
              element={<ListItems searchQuery={searchQuery} selectedCategory={selectedCategory} />} 
            />
            <Route path="/items/:id" element={<DetailedListItem />} />
          </Routes>
        </Suspense>
      </ErrorBoundary>
    </ItemProvider>
  );
};

export default App;
