import React, { forwardRef } from 'react';
import { Button } from '../../../../shared/Button';
import { ListItemProps } from '../../../../types/ItemTypes';
import { TextConstants } from '../../../../utils/constants';
import './styles.scss';

export const ListItem = forwardRef<HTMLDivElement, ListItemProps>(
  ({ item, onView, onEdit, onDelete }) => {
    const handleDeleteClick = (e: React.MouseEvent) => {
      e.stopPropagation();
      onDelete(item.id);
    };

    return (
      <div className="item" onClick={() => onView(item.id)}>
        <button 
          className="delete-button" 
          onClick={handleDeleteClick} 
          aria-label={`Delete ${item.title}`}
        >
          Ã—
        </button>
        <img 
          src={item.thumbnail} 
          alt={item.title} 
          className="item-thumbnail" 
          onError={(e) => {
            e.currentTarget.src = '/path/to/default-image.png'; // Fallback image
          }}
        />
        <div className="item-details">
          <h2 className="item-title">{item.title}</h2>
          <p className="item-description">{item.description}</p>
          <Button 
            onClick={(e) => {
              e.stopPropagation();
              onEdit(item);
            }}
          >
            {TextConstants.EDIT_ITEM}
          </Button>
        </div>
      </div>
    );
  }
);

ListItem.displayName = 'ListItem'; // Set display name for better debugging
