import { useEffect, useState, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useItemContext } from '../../context/ItemContext';
import { fetchItems } from '../../services/api';
import { Item } from '../../types/ItemTypes';
import { useInfiniteScroll } from '../../hooks/useInfiniteScroll';
import "./sstyles.scss";
export const ListItems = () => {
  const { state, dispatch } = useItemContext();
  const [page, setPage] = useState(1);

  useEffect(() => {
    const loadItems = async () => {
      const items = await fetchItems(page) as Item[];
      items.forEach((item: Item) => {
        dispatch({ type: 'ADD_ITEM', payload: item });
      });
    };

    loadItems();
  }, [page, dispatch]);

  const loadMoreItems = useCallback(() => {
    setPage(prevPage => prevPage + 1);
  }, []);

  const lastElementRef = useInfiniteScroll(loadMoreItems);

  return (
    <div className="item-list">
      {state.items.map((item: Item, index: number) => (
        <Link to={`/item/${item.id}`} key={item.id}>
          <div className="item" ref={index === state.items.length - 1 ? lastElementRef : null}>
            <img src={item.thumbnail} alt={item.title} />
            <h2>{item.title}</h2>
            <p>{item.description}</p>
          </div>
        </Link>
      ))}
    </div>
  );
};
