import React, { createContext, useReducer, useContext, ReactNode } from 'react';
import { Action, ItemState } from '../types/ItemTypes';

const initialState: ItemState = {
  items: []
};

const ItemContext = createContext<{ state: ItemState; dispatch: React.Dispatch<Action> } | undefined>(undefined);

const itemReducer = (state: ItemState, action: Action): ItemState => {
  switch (action.type) {
    case 'ADD_ITEM':
      return { items: [...state.items, action.payload] };
    case 'UPDATE_ITEM':
      return { items: state.items.map(item => item.id === action.payload.id ? action.payload : item) };
    case 'DELETE_ITEM':
      return { items: state.items.filter(item => item.id !== action.payload.id) };
    default:
      throw new Error('Unhandled action type');
  }
};

export const ItemProvider = ({ children }: { children: ReactNode }) => {
  const [state, dispatch] = useReducer(itemReducer, initialState);
  return (
    <ItemContext.Provider value={{ state, dispatch }}>
      {children}
    </ItemContext.Provider>
  );
};

export const useItemContext = () => {
  const context = useContext(ItemContext);
  if (context === undefined) {
    throw new Error('useItemContext must be used within an ItemProvider');
  }
  return context;
};
