import React, { useEffect, useState } from 'react';
import './styles.scss';
import { Item, useItemContext } from '../../context/ItemContext';
import { fetchItems } from '../../services/api';
import { Modal } from '../../shared/Modal';
import { CreateOrEditItem } from '../../components/_ListItems/CreateOrEditItem';
import { Button } from '../../shared/Button';
import { TextConstants } from '../../utils/constants';
import { Loading } from '../../shared/Loading';
import { useNavigate } from 'react-router';

export const ListItem: React.FC = () => {
  const navigate = useNavigate();
  const { state, dispatch } = useItemContext();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState<Item | undefined>(undefined);

  useEffect(() => {
    const loadItems = async () => {
      dispatch({ type: 'SET_LOADING', payload: true });
      const items = await fetchItems();
      dispatch({ type: 'SET_ITEMS', payload: items as Item[] });
      dispatch({ type: 'SET_LOADING', payload: false });
    };

    loadItems();
  }, [dispatch]);

  const handleAddItem = () => {
    setSelectedItem(undefined);
    setIsModalOpen(true);
  };

  const handleEditItem = (item: Item) => (event: React.MouseEvent<HTMLButtonElement>) => {
    event.stopPropagation();
    setSelectedItem(item);
    setIsModalOpen(true);
  };

  const handleSaveItem = (item: Item) => {
    if (selectedItem) {
      dispatch({ type: 'UPDATE_ITEM', payload: item });
    } else {
      dispatch({ type: 'ADD_ITEM', payload: item });
    }
    setIsModalOpen(false);
  };

  const handleRemoveItem = (id: number) => (event: React.MouseEvent<HTMLButtonElement>) => {
    event.stopPropagation();
    dispatch({ type: 'REMOVE_ITEM', payload: id });
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleViewItem = (id: number) => {
    navigate(`/items/${id}`);
  };

  return (
    <div className="item-list">
      <Button onClick={handleAddItem}>{TextConstants.ADD_ITEM}</Button>
      {state.loading ? (
        <Loading />
      ) : (
        state.items.map((item: Item) => (
          <ListItem 
            key={item.id} 
            item={item} 
            onView={handleViewItem} 
            onEdit={handleEditItem} 
            onDelete={handleRemoveItem} 
          />
        ))
      )}
      <Modal isOpen={isModalOpen} onClose={handleCloseModal}>
        <CreateOrEditItem
          item={selectedItem}
          onSave={handleSaveItem}
          onCancel={handleCloseModal}
        />
      </Modal>
    </div>
  );
};
