import { useItemContext } from '../../context/ItemContext';
import { Button } from '../../shared/Button';
import { Modal } from '../../shared/Modal';
import { Item } from '../../types/ItemTypes';
import { TextConstants } from '../../utils/constants';
import { CreateOrEditItem } from '../_ListItems/CreateOrEditItem';
import './styles.scss';

const Header: React.FC<{ onSearch: (query: string) => void; onFilter: (category: string) => void }> = ({ onSearch, onFilter }) => {

    const { state, dispatch } = useItemContext();
    const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        onSearch(e.target.value);
    };

    const handleFilterChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        onFilter(e.target.value);
    };

    const closeModal = () => {
        dispatch({ type: 'CLOSE_MODAL' });
    };

    const openModal = (item: Item) => {
        dispatch({ type: 'OPEN_MODAL', payload: item });
    };

    const handleCloseModal = () => {
        dispatch({ type: 'CLOSE_MODAL' });
    };

    const handleSaveItem = (item: Item) => {
        if (state.selectedItem) {
            dispatch({ type: 'UPDATE_ITEM', payload: item });
        } else {
            dispatch({ type: 'ADD_ITEM', payload: item });
        }
        dispatch({ type: 'CLOSE_MODAL' });
    };

    const handleAddItem = () => {
        openModal({} as Item);
    };

    return (
        <header className="header">
            <h1 className="header-title">My Sticky Notes</h1>
            <div className="header-controls">
                <input
                    type="text"
                    placeholder="Search..."
                    className="search-input"
                    onChange={handleSearchChange}
                />
                <select className="filter-select" onChange={handleFilterChange}>
                    <option value="">All Categories</option>
                    <option value="category1">Category 1</option>
                    <option value="category2">Category 2</option>
                    <option value="category3">Category 3</option>
                </select>
                <Button onClick={handleAddItem}>{TextConstants.ADD_ITEM}</Button>

            </div>
            <Modal isOpen={state.isModalOpen} onClose={handleCloseModal}>
                <CreateOrEditItem
                    item={state.selectedItem}
                    onSave={handleSaveItem}
                    onCancel={handleCloseModal}
                />
            </Modal>
        </header>
    );
};

export default Header;
