import { Button } from '../../../shared/Button';
import { ItemCardProps } from '../../../types/ItemTypes';
import './styles.css'; 

export const ListItem: React.FC<ItemCardProps> = ({ item, onView, onEdit, onDelete }) => {
  return (
    <div key={item.id} className="item-card" onClick={() => onView(item.id)}>
      <button className="delete-button" onClick={(e) => { e.stopPropagation(); onDelete(item.id); }}>Ã—</button>
      <img src={item.thumbnail} alt={item.title} className="item-thumbnail" />
      <div className="item-details">
        <h2 className="item-title">{item.title}</h2>
        <p className="item-description">{item.description}</p>
        <Button onClick={(e) => { e.stopPropagation(); onEdit(item)(e); }}>Edit</Button>
      </div>
    </div>
  );
};
