import React, { useEffect, useState } from 'react';
import './styles.scss';
import { useItemContext } from '../../context/ItemContext';
import { fetchItems } from '../../services/api';
import { Modal } from '../../shared/Modal';
import { CreateOrEditItem } from '../../components/_ListItems/CreateOrEditItem';
import { Button } from '../../shared/Button';
import { TextConstants } from '../../utils/constants';
import { Loading } from '../../shared/Loading';
import { useNavigate } from 'react-router';
import { ListItem } from '../../components/_ListItems/ListItem';
import { Item } from '../../types/ItemTypes';
interface ListItemsProps {
  searchQuery: string;
  selectedCategory: string;
}
const ListItems: React.FC<ListItemsProps> = ({ searchQuery, selectedCategory }) => {
  const navigate = useNavigate();
  const { state, dispatch } = useItemContext();

  const filteredItems = state.items.filter(item => {
    const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory ? item.category === selectedCategory : true;
    return matchesSearch && matchesCategory;
  });

  useEffect(() => {
    const loadItems = async () => {
      dispatch({ type: 'SET_LOADING', payload: true });
      const items = await fetchItems();
      dispatch({ type: 'SET_ITEMS', payload: items as Item[] });
      dispatch({ type: 'SET_LOADING', payload: false });
    };

    loadItems();
  }, [dispatch]);

  const handleEditItem = (item: Item) => {
    dispatch({ type: 'OPEN_MODAL', payload: item });
  };

  const handleSaveItem = (item: Item) => {
    if (state.selectedItem) {
      dispatch({ type: 'UPDATE_ITEM', payload: item });
    } else {
      dispatch({ type: 'ADD_ITEM', payload: item });
    }
    dispatch({ type: 'CLOSE_MODAL' });
  };

  const handleRemoveItem = (id: number) => {
    dispatch({ type: 'REMOVE_ITEM', payload: id });
  };

  const handleCloseModal = () => {
    dispatch({ type: 'CLOSE_MODAL' });
  };

  const handleViewItem = (id: number) => {
    navigate(`/items/${id}`);
  };

  return (
    <div className="item-list">
      {state.loading ? (
        <Loading />
      ) : (
        filteredItems.map((item: Item) => (
          <ListItem 
            key={item.id} 
            item={item} 
            onView={handleViewItem} 
            onEdit={handleEditItem} 
            onDelete={handleRemoveItem} 
          />
        ))
      )}
      <Modal isOpen={state.isModalOpen} onClose={handleCloseModal}>
        <CreateOrEditItem
          item={state.selectedItem}
          onSave={state.selectedItem ? handleSaveItem : handleEditItem}
          onCancel={handleCloseModal}
        />
      </Modal>
    </div>
  );
};

export default ListItems;
