// Ensure the path is correct

import { useNavigate } from "react-router";
import { useItemContext } from "../context/ItemContext";
import { useEffect, useMemo } from "react";
import { Item } from "../types/ItemTypes";
import { fetchItems } from "../services/api";
import { ListItem } from "../components/_ListItems/ListItems/ListItem";
import { Loading } from "../shared/Loading";

export const ListItems: React.FC = () => {
  const navigate = useNavigate();
  const { state, dispatch } = useItemContext();
  const { searchQuery, selectedCategory, currentPage, hasMore, loading } = state;

  const filteredItems = useMemo(() => {
    return state.items.filter((item: Item)=> {
      const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesCategory = selectedCategory ? item.category === selectedCategory : true;
      return matchesSearch && matchesCategory;
    });
  }, [state.items, searchQuery, selectedCategory]);

  const loadItems = async () => {
    if (!hasMore || loading) return;

    dispatch({ type: 'SET_LOADING', payload: true });
    const items = await fetchItems(currentPage) as Item[];

    dispatch({ type: 'SET_ITEMS', payload: items });
    dispatch({ type: 'INCREMENT_PAGE' });

    if (items.length === 0) {
      dispatch({ type: 'SET_HAS_MORE', payload: false });
    }

    dispatch({ type: 'SET_LOADING', payload: false });
  };

  const handleEditItem = (item: Item) => {
    dispatch({ type: 'OPEN_MODAL', payload: item });
  };

  const handleRemoveItem = (id: number) => {
    dispatch({ type: 'REMOVE_ITEM', payload: id });
  };

  const handleViewItem = (id: number) => {
    navigate(`/items/${id}`);
  };

  // Use the infinite scroll hook to load more items
  const lastItemRef = useInfiniteScroll(loadItems);

  useEffect(() => {
    loadItems(); // Initial load
  }, [selectedCategory, searchQuery]); // Only load when category or search changes

  return (
    <>
      {filteredItems.map((item: Item, index: number) => (
        <ListItem
          key={item.id} // Use unique id instead of index for better performance
          item={item}
          onView={() => handleViewItem(item.id)}
          onEdit={() => handleEditItem(item)}
          onDelete={() => handleRemoveItem(item.id)}
          ref={index === filteredItems.length - 1 ? lastItemRef : null} // Attach ref to last item
        />
      ))}
      {loading && <Loading />}
    </>
  );
};
function useInfiniteScroll(loadItems: () => Promise<void>) {
  throw new Error("Function not implemented.");
}
