import React, { createContext, useReducer } from 'react';
import { ItemProviderProps } from '../types/contextTypes';

export interface Item {
  id: number;
  title: string;
  thumbnail: string;
  description: string;
  fullDescription: string;
  category: string;
  rating: number;
}

type State = {
  items: Item[];
  loading?: boolean;
  detailedItem?: Item;
};

type Action =
  | { type: 'SET_ITEMS'; payload: Item[] }
  | { type: 'SET_DETAILED_ITEM'; payload: Item }
  | { type: 'ADD_ITEM'; payload: Item }
  | { type: 'UPDATE_ITEM'; payload: Item }
  | { type: 'REMOVE_ITEM'; payload: number }
  | { type: 'SET_LOADING'; payload: boolean };

const initialState: State = {
  items: [],
};

const ItemContext = createContext<{
  state: State;
  dispatch: React.Dispatch<Action>;
}>({
  state: initialState,
  dispatch: () => undefined,
});

const itemReducer = (state: State, action: Action): State => {
  switch (action.type) {
    case 'SET_ITEMS':
      return { ...state, items: action.payload as Item[] };
    case 'SET_DETAILED_ITEM':
      return { ...state, detailedItem: action.payload as Item};
    case 'ADD_ITEM':
      return { ...state, items: [...state.items, action.payload as Item] };
    case 'UPDATE_ITEM':
      return {
        ...state,
        items: state.items.map(item =>
          item.id === action.payload.id ? action.payload : item as Item
        ),
      };
    case 'REMOVE_ITEM':
      return {
        ...state,
        items: state.items.filter(item => item.id !== action.payload),
      };
    case 'SET_LOADING':
      return { ...state, loading: action.payload as boolean };
    default:
      return state;
  }
};

export const ItemProvider: React.FC<ItemProviderProps> = ({ children }) => {
  const [state, dispatch] = useReducer(itemReducer, initialState);

  return (
    <ItemContext.Provider value={{ state, dispatch }}>
      {children}
    </ItemContext.Provider>
  );
};


export const useItemContext = () => {
  const context = React.useContext(ItemContext);
  if (!context) {
    throw new Error('useItemContext must be used within an ItemProvider');
  }
  return context;
};
